import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Container, Button, Alert, Spinner, Card, Row, Col } from 'react-bootstrap'
import Layout from '../components/Layout'
import { Link } from 'react-router-dom'

function Users() {
  const [users, setUsers] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [filter, setFilter] = useState('all')
  const [deleting, setDeleting] = useState(null)

  useEffect(() => {
    fetchUserList()
  }, [])

  const fetchUserList = async () => {
    try {
      const token = localStorage.getItem('access_token')
      if (!token) {
        setError('Please log in to view users')
        setLoading(false)
        return
      }
      const { data } = await axios.get('http://127.0.0.1:8000/api/users/', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
      setUsers(data || [])
      setError(null)
    } catch (err) {
      console.error('Failed to load users:', err)
      setError(err.response?.status === 401 ? 'Session expired. Please login again.' : 'Could not load users')
    } finally {
      setLoading(false)
    }
  }

  const handleDelete = async (userId, username) => {
    if (!window.confirm(`Delete user "${username}"? This cannot be undone.`)) return
    
    setDeleting(userId)
    try {
      const token = localStorage.getItem('access_token')
      await axios.delete(`http://127.0.0.1:8000/api/users/${userId}/`, {
        headers: { 'Authorization': `Bearer ${token}` }
      })
      setUsers(users.filter(u => u.id !== userId))
    } catch (err) {
      setError(`Failed to delete ${username}`)
    } finally {
      setDeleting(null)
    }
  }

  const filteredUsers = filter === 'admin' 
    ? users.filter(u => u.isAdmin) 
    : filter === 'regular'
    ? users.filter(u => !u.isAdmin)
    : users

  if (loading) {
    return (
      <Layout>
        <Container style={{ marginTop: '60px', marginBottom: '100px', textAlign: 'center' }}>
          <Spinner animation="border" role="status" />
          <p className="mt-3">Loading users...</p>
        </Container>
      </Layout>
    )
  }

  return (
    <Layout>
      <Container style={{ marginTop: '40px', marginBottom: '80px' }}>
        <div className="users-header" style={{ marginBottom: '40px' }}>
          <h2>Team Members</h2>
          <p className="text-muted">Manage user accounts and permissions</p>
        </div>
        
        {error && (
          <Alert variant="warning" dismissible onClose={() => setError(null)} style={{ marginBottom: '20px' }}>
            {error}
          </Alert>
        )}

        {users.length > 0 && (
          <div style={{ marginBottom: '20px', display: 'flex', gap: '10px' }}>
            <Button 
              variant={filter === 'all' ? 'primary' : 'outline-primary'} 
              size="sm"
              onClick={() => setFilter('all')}
            >
              All ({users.length})
            </Button>
            <Button 
              variant={filter === 'admin' ? 'primary' : 'outline-primary'} 
              size="sm"
              onClick={() => setFilter('admin')}
            >
              Admins ({users.filter(u => u.isAdmin).length})
            </Button>
            <Button 
              variant={filter === 'regular' ? 'primary' : 'outline-primary'} 
              size="sm"
              onClick={() => setFilter('regular')}
            >
              Users ({users.filter(u => !u.isAdmin).length})
            </Button>
          </div>
        )}
        
        {filteredUsers.length === 0 ? (
          <Alert variant="info">No users found</Alert>
        ) : (
          <Row>
            {filteredUsers.map((user) => (
              <Col md={6} lg={4} key={user.id} style={{ marginBottom: '20px' }}>
                <Card className="h-100" style={{ border: '1px solid #e0e0e0', boxShadow: '0 1px 3px rgba(0,0,0,.08)' }}>
                  <Card.Body>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '12px' }}>
                      <div>
                        <Card.Title style={{ marginBottom: '4px', fontSize: '18px' }}>
                          {user.name || user.username}
                        </Card.Title>
                        {user.isAdmin && (
                          <span style={{ 
                            display: 'inline-block',
                            backgroundColor: '#ff9800',
                            color: 'white',
                            padding: '2px 8px',
                            borderRadius: '12px',
                            fontSize: '11px',
                            fontWeight: 'bold'
                          }}>
                            ADMIN
                          </span>
                        )}
                      </div>
                    </div>
                    <p style={{ fontSize: '13px', color: '#666', marginBottom: '8px' }}>@{user.username}</p>
                    <p style={{ fontSize: '13px', marginBottom: '16px', color: '#555' }}>{user.email}</p>
                  </Card.Body>
                  <Card.Footer style={{ backgroundColor: '#f9f9f9', border: 'none' }}>
                    <div style={{ display: 'flex', gap: '8px' }}>
                      <Link to={`/users/${user.id}`} style={{ flex: 1 }}>
                        <Button variant="outline-primary" size="sm" style={{ width: '100%' }}>
                          Profile
                        </Button>
                      </Link>
                      <Button 
                        variant="outline-danger" 
                        size="sm"
                        onClick={() => handleDelete(user.id, user.username)}
                        disabled={deleting === user.id}
                        style={{ minWidth: '80px' }}
                      >
                        {deleting === user.id ? <Spinner size="sm" /> : 'Remove'}
                      </Button>
                    </div>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </Container>
    </Layout>
  )
}

export default Users
