import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Row, Col } from 'react-bootstrap'
import Layout from '../components/Layout'
import { Link } from 'react-router-dom'

function Users() {
  const [users, setUsers] = useState([])

  useEffect(() => {
    async function fetchUsers() {
      const token = localStorage.getItem('access_token')
      const { data } = await axios.get('http://127.0.0.1:8000/api/users/', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
      setUsers(data)
    }
    fetchUsers()
  }, [])

  return (
    <Layout>
      <div style={{ padding: '30px 15px' }}>
        <h2 style={{ marginBottom: '30px', fontWeight: 'bold' }}>Team Members</h2>

        <Row>
          {users.map((user) => (
            <Col key={user.id} sm={12} md={6} lg={4} style={{ marginBottom: '24px' }}>
              <Link to={`/users/${user.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <div style={{
                  backgroundColor: 'white',
                  border: '1px solid #e0e0e0',
                  borderRadius: '8px',
                  padding: '20px',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  height: '100%',
                  minHeight: '180px',
                  display: 'flex',
                  flexDirection: 'column',
                  boxShadow: '0 1px 2px rgba(0,0,0,0.05)'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.boxShadow = '0 4px 8px rgba(0,0,0,0.12)'
                  e.currentTarget.style.transform = 'translateY(-2px)'
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.boxShadow = '0 1px 2px rgba(0,0,0,0.05)'
                  e.currentTarget.style.transform = 'translateY(0)'
                }}
                >
                  <h5 style={{ marginBottom: '5px', fontWeight: '600' }}>
                    {user.name || user.username}
                  </h5>
                  <p style={{ fontSize: '12px', color: '#999', marginBottom: '12px' }}>
                    @{user.username}
                  </p>
                  <p style={{ fontSize: '13px', color: '#555', marginBottom: 'auto', lineHeight: '1.5' }}>
                    {user.email}
                  </p>
                  {user.isAdmin && (
                    <div style={{ marginTop: '12px' }}>
                      <span style={{
                        display: 'inline-block',
                        backgroundColor: '#ff9800',
                        color: 'white',
                        padding: '4px 10px',
                        borderRadius: '10px',
                        fontSize: '11px',
                        fontWeight: 'bold'
                      }}>
                        ADMIN
                      </span>
                    </div>
                  )}
                </div>
              </Link>
            </Col>
          ))}
        </Row>
      </div>
    </Layout>
  )
}

export default Users
