import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Row, Col } from 'react-bootstrap'
import Layout from '../components/Layout'
import { Link } from 'react-router-dom'
import './Users.css'

function Users() {
  const [users, setUsers] = useState([])

  useEffect(() => {
    async function fetchUsers() {
      const token = localStorage.getItem('access_token')
      const { data } = await axios.get('http://127.0.0.1:8000/api/users/', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
      setUsers(data)
    }
    fetchUsers()
  }, [])

  return (
    <Layout>
      <div className="users-container">
        <h2 className="users-title">Team Members</h2>

        <Row>
          {users.map((user) => (
            <Col key={user.id} sm={12} md={6} lg={4}>
              <Link to={`/users/${user.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <div className="user-card">
                  <h5 className="user-name">
                    {user.name || user.username}
                  </h5>
                  <p className="user-username">
                    @{user.username}
                  </p>
                  <p className="user-email">
                    {user.email}
                  </p>
                  {user.isAdmin && (
                    <div>
                      <span className="user-admin-badge">
                        ADMIN
                      </span>
                    </div>
                  )}
                </div>
              </Link>
            </Col>
          ))}
        </Row>
      </div>
    </Layout>
  )
}

export default Users
