import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Container, Button, Form, Alert, Row, Col, Spinner, Card } from 'react-bootstrap'
import Layout from '../components/Layout'
import { useParams, useNavigate } from 'react-router-dom'

function UserProfile() {
  const { userId } = useParams()
  const navigate = useNavigate()
  const [user, setUser] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [success, setSuccess] = useState(null)
  const [editMode, setEditMode] = useState(false)
  const [saving, setSaving] = useState(false)
  const [formData, setFormData] = useState({
    email: '',
    first_name: ''
  })

  useEffect(() => {
    loadProfile()
  }, [userId])

  const loadProfile = async () => {
    try {
      setLoading(true)
      const token = localStorage.getItem('access_token')
      if (!token) {
        setError('You need to be logged in')
        setLoading(false)
        return
      }

      let url
      if (userId) {
        url = `http://127.0.0.1:8000/api/users/${userId}/`
      } else {
        url = 'http://127.0.0.1:8000/api/users.user/profile/'
      }

      const { data } = await axios.get(url, {
        headers: { 'Authorization': `Bearer ${token}` }
      })

      setUser(data)
      setFormData({
        email: data.email || '',
        first_name: data.name || data.first_name || ''
      })
      setError(null)
    } catch (err) {
      console.error('Profile load error:', err)
      setError(
        err.response?.status === 404 
          ? 'User not found' 
          : err.response?.status === 401
          ? 'Please login again'
          : 'Could not load profile'
      )
    } finally {
      setLoading(false)
    }
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData(prev => ({ ...prev, [name]: value }))
  }

  const handleSave = async (e) => {
    e.preventDefault()
    setSaving(true)
    try {
      const token = localStorage.getItem('access_token')
      const url = userId 
        ? `http://127.0.0.1:8000/api/users/${userId}/`
        : 'http://127.0.0.1:8000/api/users.user/profile/'

      const payload = {
        email: formData.email,
        first_name: formData.first_name
      }

      const { data } = await axios.put(url, payload, {
        headers: { 'Authorization': `Bearer ${token}` }
      })

      setUser(data)
      setSuccess('Profile updated!')
      setEditMode(false)
      setTimeout(() => setSuccess(null), 3000)
    } catch (err) {
      setError(err.response?.data?.detail || 'Failed to update profile')
    } finally {
      setSaving(false)
    }
  }

  const handleRemove = async () => {
    if (!window.confirm('Delete this account? This cannot be reversed.')) return

    try {
      const token = localStorage.getItem('access_token')
      const url = userId 
        ? `http://127.0.0.1:8000/api/users/${userId}/`
        : 'http://127.0.0.1:8000/api/users.user/profile/'

      await axios.delete(url, {
        headers: { 'Authorization': `Bearer ${token}` }
      })

      navigate('/users')
    } catch (err) {
      setError('Could not delete account')
    }
  }

  if (loading) {
    return (
      <Layout>
        <Container style={{ marginTop: '100px', textAlign: 'center' }}>
          <Spinner animation="border" />
          <p className="mt-3">Loading profile...</p>
        </Container>
      </Layout>
    )
  }

  if (!user) {
    return (
      <Layout>
        <Container style={{ marginTop: '60px' }}>
          <Alert variant="danger">{error || 'User not found'}</Alert>
          <Button onClick={() => navigate('/users')} variant="secondary">Back</Button>
        </Container>
      </Layout>
    )
  }

  return (
    <Layout>
      <Container style={{ marginTop: '40px', marginBottom: '80px' }}>
        <Row>
          <Col lg={7} className="mx-auto">
            <Button 
              onClick={() => navigate('/users')} 
              variant="link" 
              style={{ marginBottom: '20px', paddingLeft: 0 }}
            >
              ← Back to Users
            </Button>

            {error && (
              <Alert 
                variant="danger" 
                dismissible 
                onClose={() => setError(null)}
                style={{ marginBottom: '20px' }}
              >
                {error}
              </Alert>
            )}

            {success && (
              <Alert variant="success" style={{ marginBottom: '20px' }}>
                {success}
              </Alert>
            )}

            <Card style={{ border: '1px solid #e0e0e0', boxShadow: '0 2px 4px rgba(0,0,0,.1)' }}>
              <Card.Body style={{ padding: '30px' }}>
                <div style={{ marginBottom: '30px' }}>
                  <h3 style={{ marginBottom: '8px' }}>{user.name || user.username}</h3>
                  <p style={{ color: '#888', marginBottom: 0 }}>@{user.username}</p>
                  {user.isAdmin && (
                    <span style={{
                      display: 'inline-block',
                      marginTop: '8px',
                      backgroundColor: '#ff9800',
                      color: 'white',
                      padding: '4px 12px',
                      borderRadius: '14px',
                      fontSize: '12px',
                      fontWeight: 'bold'
                    }}>
                      Administrator
                    </span>
                  )}
                </div>

                {!editMode ? (
                  <div>
                    <div style={{ marginBottom: '20px', paddingBottom: '20px', borderBottom: '1px solid #eee' }}>
                      <p style={{ color: '#666', fontSize: '14px', marginBottom: '4px' }}>Email</p>
                      <p style={{ fontSize: '16px', marginBottom: 0 }}>{user.email}</p>
                    </div>

                    <div style={{ marginBottom: '20px', paddingBottom: '20px', borderBottom: '1px solid #eee' }}>
                      <p style={{ color: '#666', fontSize: '14px', marginBottom: '4px' }}>Full Name</p>
                      <p style={{ fontSize: '16px', marginBottom: 0 }}>{user.name || '—'}</p>
                    </div>

                    <div style={{ display: 'flex', gap: '10px', marginTop: '30px' }}>
                      <Button 
                        onClick={() => setEditMode(true)}
                        variant="primary"
                      >
                        Edit Info
                      </Button>
                      <Button 
                        onClick={handleRemove}
                        variant="outline-danger"
                      >
                        Delete Account
                      </Button>
                    </div>
                  </div>
                ) : (
                  <Form onSubmit={handleSave}>
                    <Form.Group className="mb-4">
                      <Form.Label style={{ fontWeight: '600' }}>Full Name</Form.Label>
                      <Form.Control 
                        type="text"
                        name="first_name"
                        value={formData.first_name}
                        onChange={handleChange}
                        placeholder="Your name"
                      />
                    </Form.Group>

                    <Form.Group className="mb-4">
                      <Form.Label style={{ fontWeight: '600' }}>Email Address</Form.Label>
                      <Form.Control 
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        placeholder="your@email.com"
                      />
                    </Form.Group>

                    <div style={{ display: 'flex', gap: '10px' }}>
                      <Button 
                        type="submit"
                        variant="success"
                        disabled={saving}
                      >
                        {saving ? <Spinner size="sm" className="me-2" /> : null}
                        Save Changes
                      </Button>
                      <Button 
                        onClick={() => setEditMode(false)}
                        variant="outline-secondary"
                        disabled={saving}
                      >
                        Cancel
                      </Button>
                    </div>
                  </Form>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </Layout>
  )
}

export default UserProfile
